package hackerrank.algorithm.recursion.medium;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

import static org.hamcrest.CoreMatchers.is;
import static org.hamcrest.MatcherAssert.assertThat;

class RecursiveDigitSumTest {

    static Stream<Arguments> testSuperDigit() {
        return Stream.of(
                Arguments.of("9875", 4, 8),
                Arguments.of("148", 3, 3),
                Arguments.of("123", 3, 9),
                Arguments.of("", 100000, 9)
        );
    }

    @ParameterizedTest
    @MethodSource
    void testSuperDigit(String n, int k, int expected) {
        // given

        // when
        int actual = RecursiveDigitSum.superDigit(n, k);

        // then
        assertThat(actual, is(expected));
    }
}